    <script>
        document.addEventListener("DOMContentLoaded", function() {
            // Verifica se a URL atual é "https://conclinica.com.br/quizz/"
            if (window.location.href === "https://conclinica.com.br/quizz/") {
                const formContainer = document.querySelector(".elementor-widget-form");
                const quizContainer = document.getElementById("quiz-container");

                // Seleciona o quadrado verde do formulário
                const greenBox = document.querySelector(".elementor-element-5df2efc5");

                // Seleciona o título do quiz
                const quizTitle = document.querySelector(".quiz-title");

                // Novo texto para o título do quiz
                const newText =
                    "Leia atentamente as perguntas abaixo e escolha a opção que melhor representa o contexto da sua clínica. Seja sincero em suas respostas para obter um resultado preciso. O resultado revelará o nível de maturidade da gestão do seu negócio, assim como os pontos fortes e áreas que podem ser aprimoradas.";

                // Observador para detectar mudanças no DOM
                const observer = new MutationObserver(function(mutations) {
                    mutations.forEach(function(mutation) {
                        if (mutation.addedNodes.length > 0) {
                            mutation.addedNodes.forEach(function(node) {
                                if (node.nodeType === 1 && node.classList.contains(
                                        'elementor-message-success')) {
                                    // Esconde o formulário e mostra o quiz
                                    formContainer.style.display = 'none';
                                    quizContainer.style.display = 'block';

                                    // Esconde o quadrado verde do formulário
                                    if (greenBox) {
                                        greenBox.style.display = 'none';
                                    }

                                    // Altera o texto do título do quiz
                                    if (quizTitle) {
                                        quizTitle.textContent = newText;
                                    }
                                }
                            });
                        }
                    });
                });

                // Configuração do observador
                observer.observe(formContainer, {
                    childList: true,
                    subtree: true,
                });
            }
        });
    </script>
