    <script>
        document.addEventListener("DOMContentLoaded", function() {
            // Exclui links dentro do menu e outras áreas específicas
            const excludeSelectors = ['.elementor-location-header a', '.elementor-location-footer a'];

            // Seleciona apenas os links dentro do conteúdo do post
            const contentLinks = document.querySelectorAll('.entry-content a');

            contentLinks.forEach(anchor => {
                let shouldExclude = false;

                // Verifica se o link está dentro dos seletores de exclusão
                excludeSelectors.forEach(selector => {
                    if (anchor.closest(selector)) {
                        shouldExclude = true;
                    }
                });

                // Se não deve excluir, adiciona o target="_blank"
                if (!shouldExclude) {
                    anchor.setAttribute('target', '_blank');
