    <script type="text/javascript">
        document.addEventListener('DOMContentLoaded', function() {
            // Verifica se a URL contém '/blog/', só executa se for verdadeiro
            if (window.location.pathname.includes('/uniao-estavel/')) {
                jQuery(document).on('click', '.page-numbers', function(event) {
                    event.preventDefault();

                    // URL atual gerada pelo Elementor
                    var nextPageURL = jQuery(this).attr('href');

                    // Verifica se a URL contém o identificador do Elementor (e-page-)
                    if (nextPageURL.includes('?e-page-')) {
                        // Extraímos o número da página
                        var pageNumber = nextPageURL.match(/e-page-\w+=(\d+)/)[1];

                        // Remove qualquer ocorrência anterior de /page/XX/ na URL atual
                        var currentURL = window.location.pathname.replace(/\/page\/\d+\//, '');

                        // Verifica se a URL atual termina com uma barra (/), se não, adiciona
                        if (!currentURL.endsWith('/')) {
                            currentURL += '/';
                        }

                        // Construímos a URL correta no formato /page/[number]/
                        var newURL = currentURL + 'page/' + pageNumber + '/';

                        // Atualiza a URL na barra de endereços com history.pushState
                        if (history.pushState) {
                            history.pushState(null, null, newURL);
                        }
                    } else {
                        // Se não houver o parâmetro ?e-page-, usar a URL normal
                        if (history.pushState) {
                            history.pushState(null, null, nextPageURL);
                        }
                    }
                });
            }
        });
    </script>
